package leetcode.questions.practice;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class FindAllAnagramsInString {

	public List<Integer> findAnagrams(String s, String p) {
		int n = p.length();
		List<Integer> list = new ArrayList<>();

		for (int i = 0; i < s.length(); i++) {
			if ((n + i) <= s.length()) {
				String subStr = s.substring(i, n + i);
				if (isAnagram(subStr, p)) {
					list.add(i);
				}
			}
		}
		return list;
	}

	public boolean isAnagram(String s, String t) {
		if (s.length() != t.length()) {
			return false;
		}
		char[] str1 = s.toCharArray();
		char[] str2 = t.toCharArray();
		Arrays.sort(str1);
		Arrays.sort(str2);
		return Arrays.equals(str1, str2);
	}

	public static void main(String[] args) {
		FindAllAnagramsInString anagram = new FindAllAnagramsInString();
		List<Integer> listAns = anagram.findAnagrams("", "");
		System.out.println(listAns);
	}

}
